import { Component } from '@angular/core';
import { FormGroup, FormBuilder, EmailValidator } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { SignupPageService } from './signup-page.service';

@Component({
  selector: 'app-signup-page',
  templateUrl: './signup-page.component.html',
  styleUrls: ['./signup-page.component.css']
})
export class SignupPageComponent {
  public signUpForm !: FormGroup
  constructor(private formBuilder: FormBuilder, private http: HttpClient, private router: Router, private signupService: SignupPageService) { }

  ngOnInit(): void {
    this.signUpForm = this.formBuilder.group({
      email: [""],
      password: [""]
    })
  }

  signupUser(user: any) {
    this.signupService.signup(user).subscribe(
      (response: any) => {
        console.log('Signup successful:', response);
        // You can perform further actions, such as navigating to a login page.
      },
      (error: any) => {
        console.error('Error during signup:', error);
        // Handle the error here.
      }
    );
  }

